import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Home = () => {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchStories();
  }, []);

  const fetchStories = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/stories');
      // ✅ Correct: stories are inside response.data.data
      const fetchedStories = Array.isArray(response.data.data)
        ? response.data.data
        : [];
      setStories(fetchedStories);
    } catch (error) {
      console.error('Error fetching stories:', error);
    } finally {
      setLoading(false);
    }
  };

  const groupStoriesByCategory = () => {
    if (!Array.isArray(stories)) return {};
    const grouped = {};
    stories.forEach((story) => {
      const category = story.category || 'Uncategorized';
      if (!grouped[category]) grouped[category] = [];
      grouped[category].push(story);
    });
    return grouped;
  };

  const openStory = (storyId) => {
    navigate(`/story/${storyId}`);
  };

  if (loading) {
    return (
      <div className="home-container">
        <div style={{ textAlign: 'center', padding: '50px', color: 'white' }}>
          Loading stories...
        </div>
      </div>
    );
  }

  const groupedStories = groupStoriesByCategory();

  return (
    <div className="home-container">
      <h1 style={{ color: 'white', marginBottom: '30px', textAlign: 'center' }}>
        Web Stories
      </h1>

      {Object.keys(groupedStories).length === 0 ? (
        <div style={{ textAlign: 'center', color: '#666', padding: '50px' }}>
          No stories available. Check back later!
        </div>
      ) : (
        <div className="categories-grid">
          {Object.entries(groupedStories).map(([category, categoryStories]) => (
            <div key={category} className="category-section">
              <h2 className="category-title">{category}</h2>
              <div className="stories-grid">
                {categoryStories.map((story) => (
                  <div
                    key={story._id}
                    className="story-card"
                    onClick={() => openStory(story._id)}
                  >
                    {story.slides?.length > 0 && (
                      <img
                        src={story.slides[0].url}
                        alt={story.title}
                        className="story-preview"
                      />
                    )}
                    <div className="story-title">{story.title}</div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
